import React, { useEffect, useState } from "react";

const AnimatedPrice = () => {
  const [showOldPrice, setShowOldPrice] = useState(true);

  useEffect(() => {
    const timer = setTimeout(() => {
      setShowOldPrice(false);
    }, 2000); // change after 2 seconds
    return () => clearTimeout(timer);
  }, []);

  return (
    <div className="text-xl md:text-2xl text-gray-700 mb-6 transition-all duration-500">
      {showOldPrice ? (
        <span className="line-through text-red-500 animate-pulse">$150</span>
      ) : (
        <>
          <span className="text-pink-600 font-bold text-2xl md:text-3xl animate-bounce">
            Only $79.99
          </span>
        </>
      )}
    </div>
  );
};

export default AnimatedPrice;
